document.addEventListener("DOMContentLoaded",()=>{if("localhost"===location.hostname||"127.0.0.1"===location.hostname)var baseURL=window.location.origin;else var baseURL=window.location.origin+"/blog";class Template{constructor(templateURL,templateId,selectorIndex){this._templateURL=baseURL+templateURL,this._templateId=templateId,this._selectorIndex=selectorIndex}fetchTemplate(_templateURL,_templateId,_selectorIndex){fetch(`${_templateURL}`).then(response=>response.text()).then(html=>{let parser=new DOMParser,getSelector=document.querySelector(`${_selectorIndex}`),doc,template,clone=parser.parseFromString(html,"text/html").getElementById(`${_templateId}`).content.cloneNode(!0);getSelector.appendChild(clone)}).catch(err=>{console.log("Error: faild to catch template",err)}).finally(()=>{"footerTemplate"===_templateId&&(dropDown(),smoothScroll())})}}const NavTemplate=new Template;NavTemplate.fetchTemplate("/templates/nav.html","navTemplate",'[data-navbar="top"]');const FooterTemplate=new Template;FooterTemplate.fetchTemplate("/templates/footer.html","footerTemplate",'[data-section="footer"]');const dropDown=()=>{const getToggle=document.querySelectorAll("[data-toggle]"),getPop=document.querySelectorAll('[data-toggle="pop"]'),getToolTip=document.querySelectorAll("[data-tooltip]");for(let i=0;i<getToggle.length;i++)getToggle[i].addEventListener("click",(function(e){!1===this.hasAttribute("data-state","active")?this.setAttribute("data-state","active"):!0===this.hasAttribute("data-state","active")&&this.removeAttribute("data-state"),e.stopPropagation()}));for(let i=0;i<getToolTip.length;i++)getToolTip[i].addEventListener("click",(function(e){!1===this.hasAttribute("data-state","active")?this.setAttribute("data-state","active"):!0===this.hasAttribute("data-state","active")&&this.removeAttribute("data-state"),e.stopPropagation()}));document.addEventListener("click",(function(e){for(let i=0;i<getPop.length;i++)e.target!==getPop[i]&&getPop[i].removeAttribute("data-state");for(let i=0;i<getToolTip.length;i++)e.target!==getToolTip[i]&&getToolTip[i].removeAttribute("data-state")}))},smoothScroll=()=>{const intLinks=document.querySelectorAll("a[href^='#']");for(let i=0;i<intLinks.length;i++)intLinks[i].addEventListener("click",(function(e){e.preventDefault(),document.querySelector(this.getAttribute("href")).scrollIntoView({behavior:"smooth"})}))}});
