document.addEventListener("DOMContentLoaded",()=>{if("localhost"===location.hostname||"127.0.0.1"===location.hostname)var baseURL=window.location.origin;else var baseURL=window.location.origin+"/blog";const dropDown=()=>{const getToggle=document.querySelectorAll("[data-toggle]"),getPop=document.querySelectorAll('[data-toggle="pop"]'),getToolTip=document.querySelectorAll("[data-tooltip]");for(let i=0;i<getToggle.length;i++)getToggle[i].addEventListener("click",(function(e){!1===this.hasAttribute("data-state","active")?this.setAttribute("data-state","active"):!0===this.hasAttribute("data-state","active")&&this.removeAttribute("data-state"),e.stopPropagation()}));for(let i=0;i<getToolTip.length;i++)getToolTip[i].addEventListener("click",(function(e){!1===this.hasAttribute("data-state","active")?this.setAttribute("data-state","active"):!0===this.hasAttribute("data-state","active")&&this.removeAttribute("data-state"),e.stopPropagation()}));document.addEventListener("click",(function(e){for(let i=0;i<getPop.length;i++)e.target!==getPop[i]&&getPop[i].removeAttribute("data-state");for(let i=0;i<getToolTip.length;i++)e.target!==getToolTip[i]&&getToolTip[i].removeAttribute("data-state")}))},smoothScroll=()=>{const intLinks=document.querySelectorAll("a[href^='#']");for(let i=0;i<intLinks.length;i++)intLinks[i].addEventListener("click",(function(e){e.preventDefault(),document.querySelector(this.getAttribute("href")).scrollIntoView({behavior:"smooth"})}))};class Template{constructor(templatePath,templateId,templateSelector){this._templatePath=templatePath,this._templateId=templateId,this._templateSelector=templateSelector}fetchTemplate(){fetch(baseURL+this._templatePath).then(response=>response.text()).then(html=>{let parser=new DOMParser,getTemplateSelector=document.querySelector(this._templateSelector),doc,template,clone=parser.parseFromString(html,"text/html").getElementById(this._templateId).content.cloneNode(!0);getTemplateSelector.appendChild(clone)}).catch(err=>{console.log("Error: faild to catch template",err)}).finally(()=>{"footerTemplate"===this._templateId&&(dropDown(),smoothScroll())})}}const NavTemplate=new Template("/templates/nav.html","navTemplate",'[data-section="header"]');NavTemplate.fetchTemplate();const FooterTemplate=new Template("/templates/footer.html","footerTemplate",'[data-section="footer"]');FooterTemplate.fetchTemplate()});
