document.addEventListener("DOMContentLoaded",()=>{if("localhost"===location.hostname||"127.0.0.1"===location.hostname)var baseUrl=window.location.origin;else var baseUrl=window.location.origin+"/blog";(()=>{const footerUrl=baseUrl+"/templates/footer.html";fetch(`${footerUrl}`).then((function(response){return response.text()})).then((function(html){let parser=new DOMParser,getFooter=document.querySelector('[data-section="footer"]'),doc,template,clone=parser.parseFromString(html,"text/html").getElementById("footerTemplate").content.cloneNode(!0);getFooter.appendChild(clone)})).catch((function(err){console.log("Error: faild to catch template",err)}))})(),(()=>{const getToggle=document.querySelectorAll("[data-toggle]"),getPop=document.querySelectorAll('[data-toggle="pop"]'),getToolTip=document.querySelectorAll("[data-tooltip]");for(let i=0;i<getToggle.length;i++)getToggle[i].addEventListener("click",(function(e){!1===this.hasAttribute("data-state","active")?this.setAttribute("data-state","active"):!0===this.hasAttribute("data-state","active")&&this.removeAttribute("data-state"),e.stopPropagation()}));for(let i=0;i<getToolTip.length;i++)getToolTip[i].addEventListener("click",(function(e){!1===this.hasAttribute("data-state","active")?this.setAttribute("data-state","active"):!0===this.hasAttribute("data-state","active")&&this.removeAttribute("data-state"),e.stopPropagation()}));document.addEventListener("click",(function(e){for(let i=0;i<getPop.length;i++)e.target!==getPop[i]&&getPop[i].removeAttribute("data-state");for(let i=0;i<getToolTip.length;i++)e.target!==getToolTip[i]&&getToolTip[i].removeAttribute("data-state")}))})(),(()=>{const intLinks=document.querySelectorAll("a[href^='#']");for(let i=0;i<intLinks.length;i++)intLinks[i].addEventListener("click",(function(e){e.preventDefault(),document.querySelector(this.getAttribute("href")).scrollIntoView({behavior:"smooth"})}))})()});
