document.addEventListener("DOMContentLoaded",function(){(function(){const getToggle=document.querySelectorAll(".js-toggle");const getToggleReset=document.querySelectorAll(".js-toggle-reset");for(let i=0;i<getToggle.length;i+=1){getToggle[i].addEventListener("click",function(e){if(this.classList.contains("active")===false){this.classList.add("active")}else if(this.classList.contains("active")===true){this.classList.remove("active")}e.stopPropagation()})}document.addEventListener("click",function(e){if(e.target!==getToggleReset){for(let i=0;i<getToggleReset.length;i+=1){getToggleReset[i].classList.remove("active")}}})})();(function(){const intLinks=document.querySelectorAll("a[href^='#']");for(let i=0;i<intLinks.length;i+=1){intLinks[i].addEventListener("click",function(e){e.preventDefault();document.querySelector(this.getAttribute("href")).scrollIntoView({behavior:"smooth"})})}})();});
